//Imports
import { StatusBar } from 'expo-status-bar';
//import React, { Component } from 'react';
import * as React from 'react';
import { StyleSheet, Text, View, Button, Alert} from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/native' ;
import { createMaterialBottomTabNavigator } from '@react-navigation/material-bottom-tabs';
import { MaterialCommunityIcons } from '@expo/vector-icons';

import styles from './styles/Component.style.js';

import moment from 'moment';

var date = moment().utcOffset('-05:00').format('YYYY-MM-DD hh:mm:ss a');


function Food() {
  return (
    <View style={styles.container}>
      <Text>{date}</Text>
      <Text style={styles.title}>Yo</Text>

      <Button
       color="#20232a"
       title="Click Me" 
       onPress={() => alert('Button pressed')}
       />

      <StatusBar style="auto" />
    </View>
  );
}

function Health() {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>Health!</Text>
    </View>
  );
}

function Student() {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>Student Life!</Text>
    </View>
  );
}

const Tab = createMaterialBottomTabNavigator();

function MyTabs() {
  return (
    <Tab.Navigator
      initialRouteName="Feed"
      barStyle = {{backgroundColor: '#821426'}}
      activeColor="#fff" // Color of the tab when it is clicked
      labelStyle={{ fontSize: 12 }}
      style={{ backgroundColor: 'tomato' }}
    >
      <Tab.Screen
        name="Food"
        component={Food}
        options={{
          tabBarLabel: 'Food',
          tabBarIcon: ({ color }) => (
            <MaterialCommunityIcons name="food" color={'grey'} size={26} />
          ),
        }}
      />
      <Tab.Screen
        name="Student Life"
        component={Student}
        options={{
          tabBarLabel: 'Student Life',
          tabBarIcon: ({ color }) => (
            <MaterialCommunityIcons name="school" color={'grey'} size={26} />
          ),
        }}
      />
      <Tab.Screen
        name="Health"
        component={Health}
        options={{
          tabBarLabel: 'Health',
          tabBarIcon: ({ color }) => (
            <MaterialCommunityIcons name="heart" color={'grey'} size={26} />
          ),
        }}
      />
    </Tab.Navigator>
  );
}

export default function App() {
  return (
    <NavigationContainer>
      <MyTabs />
    </NavigationContainer>
  );
}

